const _0x1e09ae=_0x51fb;function _0x30d6(){const _0x3a7699=['“Liệu\x20những\x20ký\x20ức\x20đã\x20tan,\x20hay\x20những\x20giấc\x20mơ\x20–\x20dù\x20đẹp\x20hay\x20đầy\x20ác\x20mộng\x20–\x20có\x20thể\x20tạo\x20nên\x20một\x20cá\x20thể\x20sống\x20mới?\x0a\x0aHay\x20chúng\x20chỉ\x20có\x20thể\x20lấp\x20lánh\x20trên\x20bầu\x20trời,\x20dưới\x20hình\x20dạng\x20những\x20vì\x20sao,\x20rồi\x20một\x20ngày\x20nào\x20đó\x20cũng\x20tan\x20biến\x20như\x20chưa\x20từng\x20tồn\x20tại?”\x0a\x0aĐó\x20là\x20câu\x20hỏi\x20mà\x20người\x20ta\x20vẫn\x20hay\x20thắc\x20mắc,\x20khi\x20ngẩng\x20đầu\x20nhìn\x20lên\x20trời\x20cao\x20vào\x20những\x20ngày\x20cuối\x20thu.\x20Và\x20thật\x20kỳ\x20lạ,\x20ở\x20thế\x20giới\x20này,\x20lại\x20có\x20một\x20loài\x20sinh\x20vật\x20ra\x20đời\x20từ\x20chính\x20những\x20điều\x20đó\x20–\x20từ\x20ký\x20ức,\x20từ\x20giấc\x20mơ,\x20từ\x20những\x20thứ\x20vốn\x20tưởng\x20chỉ\x20là\x20thoáng\x20qua.\x20Loài\x20ấy\x20mang\x20tên\x20Kyumulus.\x0a\x0aKhông\x20phải\x20ai\x20cũng\x20từng\x20tận\x20mắt\x20nhìn\x20thấy\x20một\x20Kyumulus,\x20nhưng\x20những\x20ai\x20từng\x20may\x20mắn\x20bắt\x20gặp\x20chúng\x20–\x20trong\x20một\x20giấc\x20mơ\x20mờ\x20sương,\x20hay\x20giữa\x20tầng\x20mây\x20xám\x20ngọc\x20–\x20sẽ\x20chẳng\x20bao\x20giờ\x20quên\x20được.\x20Bởi\x20lẽ\x20Kyumulus\x20không\x20phải\x20là\x20sinh\x20vật\x20của\x20đất,\x20cũng\x20chẳng\x20hoàn\x20toàn\x20là\x20sinh\x20vật\x20của\x20trời.\x20Chúng\x20là\x20một\x20“dạng\x20sống\x20mộng\x20tưởng”,\x20được\x20tạo\x20ra\x20từ\x20những\x20giấc\x20mơ\x20rơi\x20xuống\x20mây\x20mùa\x20thu.\x0a\x0aKhi\x20một\x20giấc\x20mơ\x20tan\x20rã\x20nơi\x20đây,\x20hoặc\x20một\x20ký\x20ức\x20mất\x20đi\x20trên\x20thế\x20gian,\x20chúng\x20không\x20biến\x20mất\x20hoàn\x20toàn.\x20Một\x20phần\x20trong\x20chúng\x20–\x20phần\x20nhẹ\x20nhất,\x20sâu\x20nhất\x20–\x20sẽ\x20trôi\x20lửng\x20lơ\x20vào\x20tầng\x20mây.\x20Và\x20nếu\x20gặp\x20đúng\x20lúc,\x20đúng\x20khí\x20thu,\x20đúng\x20sự\x20tĩnh\x20lặng\x20cần\x20thiết,\x20chúng\x20sẽ\x20tích\x20tụ\x20lại,\x20ngưng\x20tụ\x20thành\x20một\x20cá\x20thể\x20sống\x20mới:\x20một\x20Kyumulus.\x0a\x0aCơ\x20thể\x20Kyumulus\x20mềm\x20như\x20tơ\x20và\x20nhẹ\x20như\x20hơi\x20thở.\x20Chúng\x20mang\x20lớp\x20lông\x20mờ\x20ảo,\x20bồng\x20bềnh\x20như\x20mây\x20nhưng\x20vẫn\x20ấm\x20áp\x20như\x20sương\x20mai\x20còn\x20chưa\x20tan.\x20Mỗi\x20Kyumulus\x20mang\x20hình\x20dáng,\x20màu\x20sắc\x20và\x20sắc\x20thái\x20cảm\x20xúc\x20khác\x20nhau\x20–\x20bởi\x20vì\x20không\x20có\x20giấc\x20mơ\x20nào\x20giống\x20nhau,\x20cũng\x20như\x20không\x20có\x20ký\x20ức\x20nào\x20là\x20trùng\x20lặp.\x20Một\x20số\x20Kyumulus\x20toát\x20ra\x20thứ\x20ánh\x20sáng\x20dịu\x20dàng,\x20như\x20được\x20sinh\x20ra\x20từ\x20một\x20đêm\x20yên\x20bình\x20và\x20một\x20giấc\x20ngủ\x20an\x20lành.\x20Một\x20số\x20khác\x20lại\x20như\x20thể\x20đang\x20khóc,\x20run\x20rẩy,\x20luôn\x20nép\x20mình\x20trong\x20sương\x20vì\x20giấc\x20mơ\x20tạo\x20nên\x20chúng\x20đã\x20từng\x20là\x20cơn\x20ác\x20mộng.\x0a\x0aNgười\x20dân\x20tại\x20Vân\x20Quốc\x20(Cloud),\x20từ\x20lâu\x20đã\x20xem\x20Kyumulus\x20là\x20sinh\x20vật\x20linh\x20thiêng\x20–\x20vừa\x20yêu\x20quý,\x20vừa\x20tôn\x20kính,\x20vừa\x20thương\x20xót.\x20Với\x20họ,\x20Kyumulus\x20là\x20những\x20“đứa\x20con\x20của\x20trời\x20thu”,\x20là\x20phần\x20ký\x20ức\x20trôi\x20nổi\x20chưa\x20từng\x20có\x20nơi\x20chốn\x20để\x20dừng\x20lại,\x20là\x20giấc\x20mơ\x20đẹp\x20đã\x20quên\x20không\x20kịp\x20gọi\x20tên.\x20Không\x20phải\x20ai\x20cũng\x20được\x20thấy\x20Kyumulus,\x20nhưng\x20một\x20khi\x20thấy\x20rồi,\x20trái\x20tim\x20người\x20ta\x20như\x20được\x20lay\x20động\x20bởi\x20điều\x20gì\x20đó\x20vừa\x20mong\x20manh\x20vừa\x20thật\x20thà.\x20Chúng\x20không\x20có\x20lời,\x20không\x20có\x20lý\x20lẽ,\x20nhưng\x20có\x20một\x20sự\x20tồn\x20tại\x20khiến\x20người\x20khác\x20muốn\x20lắng\x20nghe.\x0a\x0aNhưng\x20Kyumulus\x20không\x20thể\x20ở\x20lại\x20lâu.\x20Sự\x20sống\x20của\x20chúng\x20được\x20duy\x20trì\x20hoàn\x20toàn\x20bằng\x20phần\x20giấc\x20mơ\x20đã\x20tạo\x20ra\x20chúng\x20–\x20và\x20giấc\x20mơ\x20thì\x20luôn\x20tàn.\x20Tuổi\x20thọ\x20của\x20Kyumulus\x20trung\x20bình\x20chỉ\x20kéo\x20dài\x20khoảng\x20hai\x20đến\x20ba\x20tháng,\x20đúng\x20bằng\x20một\x20mùa\x20thu.\x20Sau\x20đó,\x20chúng\x20bắt\x20đầu\x20mờ\x20đi.\x20Không\x20đau\x20đớn,\x20không\x20giãy\x20giụa,\x20chỉ\x20là…\x20tan\x20vào\x20mây.\x20Tựa\x20như\x20chúng\x20chưa\x20từng\x20ở\x20đây.\x0a\x0aVào\x20cuối\x20mỗi\x20mùa\x20thu,\x20khi\x20bầu\x20trời\x20chuyển\x20lạnh\x20và\x20những\x20tán\x20lá\x20rơi\x20nhiều\x20nhất,\x20lũ\x20quạ\x20sẽ\x20bay\x20đến.\x20Không\x20phải\x20quạ\x20u\x20ám\x20trong\x20những\x20câu\x20chuyện\x20báo\x20tử,\x20mà\x20là\x20những\x20sinh\x20vật\x20cổ\x20xưa\x20có\x20nhiệm\x20vụ\x20canh\x20giữ\x20vòng\x20luân\x20hồi\x20của\x20ký\x20ức.\x20Chúng\x20yêu\x20những\x20vật\x20sáng\x20lấp\x20lánh\x20–\x20và\x20thứ\x20chúng\x20đi\x20tìm\x20không\x20phải\x20xác\x20thịt\x20hay\x20máu,\x20mà\x20chính\x20là\x20những\x20ngôi\x20sao.\x0a\x0aBởi\x20mỗi\x20Kyumulus,\x20khi\x20sinh\x20ra,\x20đều\x20gắn\x20liền\x20với\x20một\x20ngôi\x20sao,\x20là\x20ánh\x20sáng\x20lưu\x20giữ\x20toàn\x20bộ\x20ký\x20ức,\x20cảm\x20xúc,\x20và\x20nguồn\x20gốc\x20giấc\x20mơ\x20của\x20chúng.\x20Khi\x20Kyumulus\x20tan\x20biến,\x20lũ\x20quạ\x20sẽ\x20tìm\x20đến\x20và\x20thu\x20hồi\x20ngôi\x20sao\x20ấy,\x20mang\x20nó\x20trở\x20lại\x20bầu\x20trời,\x20đặt\x20vào\x20đúng\x20vị\x20trí\x20giữa\x20thiên\x20hà.\x20Người\x20ta\x20gọi\x20đó\x20là\x20nghi\x20lễ\x20kết\x20thúc\x20mộng\x20mơ.\x0a\x0aThế\x20nhưng,\x20giấc\x20mơ\x20không\x20hoàn\x20toàn\x20biến\x20mất.\x20Khi\x20ngôi\x20sao\x20ấy\x20được\x20trả\x20lại\x20bầu\x20trời,\x20ánh\x20sáng\x20từ\x20nó\x20sẽ\x20dần\x20hòa\x20vào\x20vũ\x20trụ,\x20trở\x20thành\x20hạt\x20giống,\x20trôi\x20qua\x20hàng\x20nghìn\x20thế\x20giới.\x20Có\x20khi\x20phải\x20mất\x20cả\x20thế\x20kỷ,\x20có\x20khi\x20chỉ\x20một\x20giây\x20trong\x20lòng\x20ai\x20đó,\x20những\x20hạt\x20giống\x20ấy\x20sẽ\x20lại\x20rơi\x20–\x20vào\x20một\x20mùa\x20thu\x20khác,\x20vào\x20một\x20đám\x20mây\x20khác\x20–\x20và\x20một\x20Kyumulus\x20mới\x20sẽ\x20được\x20sinh\x20ra,\x20từ\x20mảnh\x20ký\x20ức\x20tưởng\x20chừng\x20đã\x20mất\x20ấy.\x0a\x0aChúng\x20cứ\x20như\x20vậy:\x20Sinh\x20ra\x20từ\x20điều\x20mơ\x20hồ,\x20biến\x20mất\x20vào\x20điều\x20vô\x20hình,\x20chờ\x20tái\x20sinh\x20bởi\x20điều\x20không\x20thể\x20đoán\x20trước.\x0a\x0aDẫu\x20vậy,\x20nếu\x20đi\x20sâu\x20hơn\x20vào\x20cấu\x20trúc\x20của\x20chúng,\x20Kyumulus\x20lại\x20hiện\x20lên\x20như\x20một\x20bi\x20kịch\x20được\x20tạo\x20hình.\x20Vì\x20chúng\x20quá\x20đẹp,\x20quá\x20thuần\x20khiết,\x20quá\x20mong\x20manh\x20–\x20đến\x20mức\x20không\x20thể\x20sống\x20nổi\x20như\x20một\x20sinh\x20vật\x20đúng\x20nghĩa.\x0a\x0aCơ\x20thể\x20của\x20chúng\x20–\x20về\x20bản\x20chất\x20–\x20là\x20mây,\x20là\x20sương,\x20là\x20ánh\x20sáng\x20lỏng.\x20Chúng\x20không\x20thể\x20đi\x20bộ\x20như\x20các\x20loài\x20khác,\x20cũng\x20không\x20thể\x20chạy,\x20không\x20thể\x20nhảy,\x20không\x20thể\x20chiến\x20đấu\x20hay\x20bảo\x20vệ\x20chính\x20mình.\x20Chúng\x20không\x20có\x20bản\x20năng\x20sinh\x20tồn,\x20cũng\x20không\x20hiểu\x20điều\x20gì\x20là\x20nguy\x20hiểm.\x20Chúng\x20chỉ\x20trôi\x20–\x20lặng\x20lẽ,\x20chậm\x20rãi\x20–\x20theo\x20gió,\x20theo\x20nắng,\x20theo\x20sương,\x20như\x20thể\x20chính\x20gió\x20trời\x20là\x20thứ\x20đã\x20vẽ\x20nên\x20chúng\x20rồi\x20cũng\x20sẽ\x20cuốn\x20chúng\x20đi.\x0a\x0aKyumulus\x20không\x20biết\x20mình\x20được\x20sinh\x20ra\x20để\x20làm\x20gì.\x20Không\x20ai\x20dạy\x20chúng\x20nói\x20chuyện.\x20Không\x20ai\x20trao\x20cho\x20chúng\x20khái\x20niệm\x20về\x20mục\x20đích\x20sống.\x20Và\x20rồi,\x20khi\x20mùa\x20thu\x20kết\x20thúc,\x20chúng\x20cứ\x20thế\x20tan\x20biến\x20–\x20không\x20để\x20lại\x20gì\x20ngoài\x20ánh\x20sáng\x20từ\x20một\x20ngôi\x20sao\x20chờ\x20được\x20quạ\x20mang\x20đi.\x0a\x0aCó\x20lẽ\x20chính\x20vì\x20ngắn\x20ngủi,\x20nên\x20Kyumulus\x20mới\x20đẹp\x20đến\x20thế.\x20Và\x20cũng\x20chính\x20vì\x20vậy,\x20nên\x20sự\x20tồn\x20tại\x20của\x20chúng\x20mới\x20mang\x20theo\x20nỗi\x20buồn\x20không\x20ai\x20dám\x20nói\x20thành\x20lời.\x0a\x0aNếu\x20như\x20được\x20sinh\x20ra,\x20chỉ\x20để\x20chết\x20đi\x20sau\x20vài\x20tháng\x20ngắn\x20ngủi,\x20và\x20cả\x20thế\x20giới\x20không\x20thể\x20nghe\x20được\x20tiếng\x20lòng\x20của\x20chúng…\x20Vậy\x20thì\x20sinh\x20ra\x20để\x20làm\x20gì?','8BRMGex','Fantasy','Chapter\x201:\x20Children\x20of\x20the\x20Autumn\x20Sky','Drama','2170500eFZgJU','Mùa\x20thu\x20dần\x20chạm\x20đến\x20rìa\x20cuối\x20của\x20mình.\x20Không\x20còn\x20là\x20những\x20ngày\x20vàng\x20rực,\x20nhẹ\x20bẫng\x20như\x20tơ\x20nữa,\x20mà\x20là\x20sự\x20chênh\x20vênh\x20trong\x20từng\x20đợt\x20gió\x20hanh\x20hao\x20cuốn\x20qua\x20tầng\x20mây.\x20Những\x20dải\x20mây\x20mỏng\x20bắt\x20đầu\x20tan\x20chảy\x20từng\x20chút\x20một\x20dưới\x20ánh\x20chiều\x20lịm\x20tắt,\x20và\x20từng\x20ngôi\x20sao\x20ký\x20ức\x20của\x20các\x20Kyumulus\x20cũng\x20lần\x20lượt\x20bị\x20quạ\x20mang\x20đi,\x20bay\x20cao,\x20bay\x20xa\x20khỏi\x20vùng\x20trời\x20từng\x20là\x20nơi\x20chúng\x20sống\x20và\x20mơ.\x0a\x0aMột\x20Kyumulus\x20đứng\x20đó,\x20chứng\x20kiến\x20mọi\x20thứ\x20như\x20một\x20vết\x20xước\x20dài\x20cứa\x20vào\x20tận\x20trong\x20lòng\x20ngực\x20mây\x20mềm\x20của\x20mình.\x20Ngày\x20qua\x20ngày,\x20nó\x20nhìn\x20những\x20sinh\x20vật\x20bồng\x20bềnh\x20quanh\x20mình\x20lần\x20lượt\x20bị\x20quạ\x20nhấc\x20đi.\x20Những\x20linh\x20hồn\x20ngập\x20tràn\x20ánh\x20sáng\x20ấy\x20không\x20hề\x20phản\x20kháng,\x20như\x20thể\x20đó\x20là\x20kết\x20thúc\x20tự\x20nhiên\x20và\x20không\x20thể\x20tránh\x20khỏi.\x20Nhưng\x20trong\x20ánh\x20mắt\x20của\x20Kyumulus\x20đó,\x20chẳng\x20có\x20lấy\x20chút\x20cam\x20chịu\x20nào,\x20chỉ\x20là\x20nỗi\x20sợ\x20hãi\x20ngày\x20một\x20lớn\x20dần,\x20giằng\x20xé\x20từng\x20lớp\x20mây\x20trên\x20thân\x20thể\x20nó.\x0a\x0aNó\x20sợ\x20cái\x20kết\x20đang\x20tiến\x20đến\x20gần.\x20Sợ\x20khoảnh\x20khắc\x20một\x20con\x20quạ\x20đen\x20sẽ\x20vươn\x20móng\x20đến,\x20xuyên\x20qua\x20thân\x20thể,\x20cuốn\x20lấy\x20vì\x20sao\x20đang\x20lấp\x20lánh\x20trong\x20tim\x20nó\x20và\x20đem\x20về\x20trời.\x20Sợ\x20đến\x20mức\x20chỉ\x20muốn\x20vỡ\x20ra\x20thành\x20sương.\x0a\x0aNhưng\x20trái\x20ngang\x20thay,\x20nó\x20cũng\x20hiểu\x20một\x20điều\x20khác.\x20Nếu\x20không\x20được\x20quạ\x20đem\x20về\x20trời,\x20ngôi\x20sao\x20của\x20nó\x20sẽ\x20không\x20thể\x20tiếp\x20tục\x20phát\x20sáng\x20sau\x20mùa\x20thu.\x20Ký\x20ức\x20của\x20nó\x20sẽ\x20tắt\x20lịm\x20như\x20ngọn\x20lửa\x20bị\x20dập\x20bằng\x20bàn\x20tay\x20vô\x20hình,\x20và\x20giấc\x20mơ\x20từng\x20có\x20mặt\x20trong\x20cõi\x20đời\x20này\x20sẽ\x20mãi\x20mãi\x20tan\x20biến,\x20như\x20chưa\x20từng\x20hiện\x20hữu.\x0a\x0aNó\x20không\x20muốn\x20chết.\x20Và\x20cũng\x20không\x20muốn\x20bị\x20lãng\x20quên.\x0a\x0aBên\x20cạnh\x20nó,\x20người\x20bạn\x20đồng\x20hành\x20vẫn\x20luôn\x20nở\x20nụ\x20cười\x20dịu\x20nhẹ.\x20Vẻ\x20bình\x20yên\x20tỏa\x20ra\x20từ\x20đôi\x20mắt\x20ấy\x20khiến\x20chính\x20những\x20cơn\x20gió\x20mùa\x20thu\x20cũng\x20như\x20dịu\x20lại\x20khi\x20lướt\x20qua.\x20Người\x20bạn\x20ấy\x20không\x20hề\x20sợ\x20hãi,\x20cũng\x20như\x20không\x20hề\x20sợ\x20phải\x20biến\x20mất,\x20không\x20sợ\x20bị\x20thu\x20thập.\x20Không\x20mong\x20đi\x20xa,\x20cũng\x20chẳng\x20mơ\x20ở\x20lại.\x20Chỉ\x20cần\x20được\x20tồn\x20tại\x20và\x20chơi\x20đùa,\x20bồng\x20bềnh\x20như\x20chiếc\x20lá\x20đang\x20rơi,\x20là\x20đã\x20đủ\x20rồi.\x20Cái\x20chết\x20–\x20với\x20người\x20bạn\x20ấy\x20–\x20chỉ\x20là\x20một\x20giấc\x20ngủ\x20dài,\x20để\x20rồi\x20mùa\x20thu\x20sau\x20lại\x20bừng\x20tỉnh\x20lần\x20nữa.\x20Nhẹ\x20nhàng,\x20không\x20vướng\x20bận.\x20Không\x20một\x20khát\x20khao\x20nào\x20kéo\x20níu\x20lấy\x20thế\x20gian.\x0a\x0aGiấc\x20mơ\x20ấy\x20đã\x20từng\x20ước\x20được\x20như\x20bạn\x20mình.\x20Đã\x20từng\x20cố\x20gắng\x20dặn\x20lòng\x20rằng\x20rồi\x20cái\x20chết\x20cũng\x20chỉ\x20là\x20một\x20điều\x20tự\x20nhiên.\x20Nhưng\x20trái\x20tim\x20mây\x20trong\x20lồng\x20ngực\x20nó\x20chẳng\x20thể\x20nào\x20nghe\x20theo\x20lý\x20trí.\x20Nỗi\x20sợ\x20cứ\x20lớn\x20dần,\x20cho\x20đến\x20khi\x20nó\x20chẳng\x20thể\x20chịu\x20đựng\x20nổi\x20nữa.\x0a\x0aRồi\x20ngày\x20định\x20mệnh\x20ấy\x20cũng\x20đến.\x20Chiều\x20hôm\x20đó,\x20mặt\x20trời\x20rơi\x20rất\x20chậm.\x20Cả\x20bầu\x20trời\x20được\x20nhuộm\x20đỏ\x20hồng,\x20như\x20một\x20dải\x20lụa\x20đẫm\x20màu\x20ly\x20biệt.\x20Trên\x20nền\x20trời\x20ấy,\x20một\x20con\x20quạ\x20sải\x20cánh\x20đến,\x20lặng\x20lẽ,\x20không\x20phát\x20ra\x20âm\x20thanh\x20gì\x20ngoài\x20tiếng\x20đập\x20cánh\x20trầm\x20trầm\x20như\x20nhịp\x20trống\x20đưa\x20tang.\x20Và\x20nó…không\x20phải\x20là\x20đứa\x20được\x20chọn.\x20Mà\x20là\x20bạn\x20của\x20nó.\x0a\x0aCái\x20khoảnh\x20khắc\x20con\x20quạ\x20đưa\x20móng\x20vuốt\x20xuống,\x20xuyên\x20qua\x20mây\x20của\x20người\x20bạn,\x20nhấc\x20bổng\x20lên\x20cùng\x20ánh\x20sao\x20sáng\x20lấp\x20lánh\x20giữa\x20ngực.\x20Kyumulus\x20ấy\x20như\x20bị\x20xé\x20làm\x20đôi.\x20Nó\x20lao\x20đến,\x20run\x20rẩy,\x20gào\x20lên\x20không\x20thành\x20tiếng.\x20Mọi\x20âm\x20thanh\x20vỡ\x20vụn\x20thành\x20gió.\x0a\x0a“Trả\x20lại\x20bạn\x20ta...\x20Làm\x20ơn…\x20Đừng\x20mang\x20đi...”\x0a\x0aNhưng\x20loài\x20quạ\x20không\x20bao\x20giờ\x20hiểu\x20được\x20cảm\x20xúc\x20của\x20Kyumulus.\x20Chúng\x20không\x20biết\x20đến\x20nước\x20mắt.\x20Chúng\x20chỉ\x20làm\x20công\x20việc\x20của\x20mình\x20–\x20thu\x20thập\x20ánh\x20sáng\x20của\x20những\x20giấc\x20mơ\x20và\x20đem\x20về\x20trời.\x0a\x0aKhi\x20ngôi\x20sao\x20của\x20người\x20bạn\x20bị\x20nhấc\x20lên,\x20không\x20một\x20chút\x20kháng\x20cự,\x20nụ\x20cười\x20vẫn\x20còn\x20đọng\x20lại\x20ở\x20đám\x20mây\x20kia.\x20Nhẹ\x20như\x20thể\x20đang\x20đi\x20vào\x20giấc\x20mơ\x20khác,\x20không\x20mảy\x20may\x20lo\x20lắng\x20điều\x20gì.\x20Nhưng\x20với\x20Kyumulus\x20còn\x20lại,\x20đó\x20là\x20tận\x20thế.\x0a\x0aTrong\x20cơn\x20tuyệt\x20vọng\x20cùng\x20cực,\x20một\x20sức\x20mạnh\x20nào\x20đó\x20bùng\x20lên\x20từ\x20bên\x20trong.\x20Thứ\x20gì\x20đó\x20xé\x20toạc\x20sự\x20yếu\x20đuối\x20của\x20nó,\x20đập\x20tan\x20cái\x20vô\x20lực\x20vốn\x20là\x20bản\x20năng\x20của\x20loài.\x20Nó\x20lao\x20lên.\x20Và\x20chỉ\x20trong\x20một\x20khoảnh\x20khắc\x20ngắn\x20ngủi,\x20con\x20quạ\x20rơi\x20xuống\x20khỏi\x20bầu\x20trời,\x20vỡ\x20vụn\x20như\x20một\x20chùm\x20tro\x20đen\x20bị\x20thiêu\x20rụi\x20giữa\x20mây.\x0a\x0aGiấc\x20mơ\x20của\x20người\x20bạn\x20rơi\x20trở\x20lại.\x20Ngôi\x20sao\x20chưa\x20kịp\x20bị\x20đem\x20đi.\x20Nó\x20vẫn\x20còn\x20sáng.\x20Vẫn\x20còn\x20sống.\x20Kyumulus\x20ấy\x20run\x20rẩy\x20tiến\x20lại\x20gần,\x20bàn\x20tay\x20bằng\x20mây\x20mềm\x20run\x20lên\x20bần\x20bật\x20khi\x20chạm\x20vào\x20ánh\x20sáng\x20ấy.\x20Nó\x20cố\x20níu\x20kéo,\x20cố\x20hòa\x20giấc\x20mơ\x20ấy\x20trở\x20lại\x20đám\x20mây\x20vừa\x20mất\x20đi\x20chủ\x20nhân,\x20nhưng…\x20đã\x20quá\x20muộn.\x20Thể\x20xác\x20người\x20bạn\x20đã\x20tan\x20đi.\x20Chỉ\x20còn\x20lại\x20ngôi\x20sao\x20rực\x20rỡ\x20không\x20nơi\x20trú\x20ngụ.\x20Nó\x20hiểu,\x20người\x20bạn\x20đó\x20sẽ\x20không\x20thể\x20sống\x20lại.\x20Và\x20thế\x20là…\x20nó\x20làm\x20điều\x20không\x20Kyumulus\x20nào\x20từng\x20nghĩ\x20đến.\x0a\x0aKhi\x20ngôi\x20sao\x20được\x20dẫn\x20vào\x20cơ\x20thể,\x20một\x20dòng\x20năng\x20lượng\x20lạ\x20tràn\x20qua\x20từng\x20tầng\x20mây\x20của\x20nó.\x20Tất\x20cả\x20những\x20gì\x20yếu\x20ớt,\x20chập\x20chờn\x20trong\x20nó\x20bỗng\x20dưng\x20cứng\x20cáp\x20lại,\x20rõ\x20ràng\x20hơn.\x20Nó\x20có\x20thể\x20bước\x20đi.\x20Không\x20chỉ\x20trôi\x20nổi\x20nữa,\x20mà\x20là\x20bước\x20thật\x20sự.\x20Cơ\x20thể\x20nó\x20nặng\x20hơn\x20một\x20chút,\x20nhưng\x20cũng\x20vững\x20chãi\x20hơn.\x20Cảm\x20giác\x20như...\x20sự\x20sống\x20vừa\x20được\x20nối\x20dài\x20thêm.\x0a\x0aLần\x20đầu\x20tiên,\x20nó\x20cảm\x20nhận\x20được\x20hy\x20vọng.\x20Không\x20phải\x20là\x20hy\x20vọng\x20mơ\x20hồ…\x20mà\x20là\x20cơ\x20hội\x20thật\x20sự\x20để\x20sống\x20tiếp.\x20Nhưng\x20cùng\x20lúc\x20ấy,\x20một\x20mối\x20nguy\x20khác\x20cũng\x20dấy\x20lên\x20từ\x20bên\x20trong.\x20Ngôi\x20sao\x20vừa\x20hấp\x20thụ\x20không\x20chỉ\x20chứa\x20sức\x20mạnh,\x20mà\x20còn\x20có\x20ký\x20ức.\x20Ý\x20thức\x20của\x20người\x20bạn\x20ấy\x20vẫn\x20còn\x20nằm\x20đâu\x20đó\x20trong\x20ánh\x20sáng,\x20có\x20thể\x20trỗi\x20dậy\x20bất\x20cứ\x20lúc\x20nào,\x20đè\x20lên\x20tâm\x20trí\x20mỏng\x20manh\x20của\x20nó.\x0a\x0aNó\x20không\x20thể\x20để\x20điều\x20đó\x20xảy\x20ra.\x20Nó\x20không\x20thể\x20mất\x20đi\x20quyền\x20làm\x20chủ.\x20Không\x20thể\x20để\x20ai\x20khác\x20sống\x20thay\x20mình,\x20dù\x20đó\x20là\x20bạn\x20thân\x20nhất.\x20Và\x20vì\x20vậy,\x20bằng\x20sức\x20mạnh\x20vừa\x20thức\x20tỉnh,\x20nó\x20phong\x20ấn\x20ý\x20thức\x20của\x20người\x20bạn\x20lại,\x20chặn\x20mọi\x20con\x20đường\x20quay\x20về.\x20Người\x20bạn\x20ấy\x20sẽ\x20không\x20thức\x20tỉnh\x20trong\x20cơ\x20thể\x20này.\x20Không\x20bao\x20giờ.\x0a\x0aKyumulus\x20ấy\x20mỉm\x20cười.\x20Không\x20phải\x20nụ\x20cười\x20giả\x20vờ.\x20Mà\x20là\x20nụ\x20cười\x20đầu\x20tiên\x20thực\x20sự\x20trong\x20đời.\x20Nó\x20cảm\x20thấy…\x20hạnh\x20phúc.\x20Không\x20phải\x20vì\x20vừa\x20cướp\x20đi\x20sinh\x20mạng\x20của\x20con\x20quạ\x20tội\x20nghiệp.\x20Không\x20phải\x20vì\x20hấp\x20thụ\x20người\x20bạn.\x20Mà\x20vì,\x20cuối\x20cùng,\x20nó\x20đã\x20tìm\x20thấy\x20một\x20con\x20đường.\x0a\x0aMột\x20kế\x20hoạch,\x20một\x20phương\x20án\x20sống\x20còn,\x20một\x20tia\x20sáng\x20lẻ\x20loi\x20trong\x20lối\x20mòn\x20của\x20loài\x20Kyumulus\x20ngắn\x20ngủi.\x0a\x0aNó\x20sẽ\x20không\x20để\x20ngôi\x20sao\x20của\x20mình\x20bị\x20thu\x20thập.\x20Nó\x20sẽ\x20không\x20tan\x20biến\x20như\x20những\x20giấc\x20mơ\x20khác.\x20Và\x20nếu\x20đến\x20ngày\x20ánh\x20sao\x20ấy\x20vụt\x20tắt…\x20Nó\x20sẽ\x20tìm\x20một\x20ánh\x20sao\x20khác.\x20Cho\x20đến\x20khi\x20mùa\x20thu\x20kế\x20tiếp\x20trở\x20lại.','Chapter\x204:\x20The\x20Path\x20of\x20a\x20Fallen\x20Star','Psychological','Iroh\x20-\x20Sonn','Chapter\x203:\x20You\x20Made\x20Me\x20Hesitate','14IEFtPe','1219031BbVTsU','5163120vGmPwl','1851615QbtElz','Mùa\x20thu\x20gần\x20như\x20đã\x20đến\x20giai\x20đoạn\x20cuối.\x20Trên\x20nền\x20trời\x20nhạt\x20màu,\x20những\x20cơn\x20gió\x20chỉ\x20còn\x20mang\x20mùi\x20lạnh\x20và\x20mảnh\x20vụn\x20của\x20hoàng\x20hôn,\x20chẳng\x20còn\x20thơm\x20như\x20thời\x20đầu\x20mùa\x20nữa.\x20Không\x20còn\x20những\x20đợt\x20mây\x20mịn\x20trôi\x20ngang\x20vòm\x20trời\x20đầy\x20sao,\x20mà\x20chỉ\x20còn\x20thưa\x20thớt\x20vài\x20sợi\x20xám\x20nhạt,\x20như\x20dấu\x20tích\x20của\x20điều\x20gì\x20đó\x20từng\x20rất\x20sống\x20động,\x20giờ\x20đã\x20rệu\x20rã.\x0a\x0aVà\x20trong\x20khung\x20cảnh\x20đó,\x20một\x20giấc\x20mơ\x20vẫn\x20còn\x20sống.\x0a\x0aKhông\x20còn\x20là\x20giấc\x20mơ\x20yếu\x20đuối\x20run\x20rẩy\x20trong\x20dòng\x20lũ\x20thời\x20gian\x20–\x20mà\x20là\x20một\x20Kyumulus\x20đứng\x20vững\x20trên\x20nền\x20đất,\x20với\x20đôi\x20mắt\x20mở\x20to,\x20rực\x20cháy\x20ánh\x20sáng\x20từ\x20hàng\x20trăm\x20ngôi\x20sao\x20đã\x20bị\x20nó\x20cưỡng\x20đoạt.\x0a\x0aNó\x20đã\x20bắt\x20đầu\x20săn\x20lũ\x20quạ.\x20Một\x20cách\x20âm\x20thầm.\x20Kiên\x20quyết.\x20Không\x20có\x20tiếc\x20nuối,\x20cũng\x20chẳng\x20còn\x20rối\x20ren\x20trong\x20lòng.\x20Từng\x20con\x20một,\x20những\x20cánh\x20chim\x20đen\x20mang\x20hình\x20hài\x20của\x20“người\x20đưa\x20tiễn”\x20bị\x20hạ\x20gục.\x20Không\x20phải\x20bằng\x20kỹ\x20năng\x20chiến\x20đấu,\x20cũng\x20chẳng\x20bằng\x20sức\x20mạnh\x20bẩm\x20sinh.\x20Chỉ\x20đơn\x20giản\x20bằng\x20sự\x20tỉnh\x20táo\x20và\x20một\x20bản\x20năng\x20sống\x20còn\x20đã\x20chạm\x20tới\x20ngưỡng\x20điên\x20cuồng.\x20Không\x20con\x20quạ\x20nào\x20còn\x20sống\x20sót\x20sau\x20khi\x20trông\x20thấy\x20nó.\x0a\x0aTừng\x20ánh\x20sao\x20ký\x20ức\x20trên\x20cơ\x20thể\x20bọn\x20quạ\x20–\x20vốn\x20là\x20những\x20vì\x20sao\x20mà\x20chúng\x20thu\x20lượm\x20từ\x20các\x20Kyumulus\x20đã\x20tan\x20biến\x20–\x20giờ\x20bị\x20cướp\x20lại,\x20thô\x20bạo\x20mà\x20chuẩn\x20xác.\x20Dù\x20phần\x20lớn\x20những\x20ngôi\x20sao\x20đó\x20chỉ\x20còn\x20rực\x20rỡ\x20thêm\x20vài\x20ngày,\x20nhiều\x20lắm\x20cũng\x20chỉ\x201\x202\x20hôm\x20nữa\x20là\x20sẽ\x20tắt\x20hẳn.\x20Nhưng\x20số\x20lượng...\x20số\x20lượng\x20chính\x20là\x20điều\x20khiến\x20tất\x20cả\x20thay\x20đổi.\x0a\x0aNó\x20không\x20còn\x20tuyệt\x20vọng\x20khi\x20giết.\x20Chẳng\x20phải\x20vì\x20đã\x20thành\x20máu\x20lạnh.\x20Mà\x20là\x20vì,\x20đây\x20là\x20con\x20đường\x20duy\x20nhất.\x0a\x0aNó\x20buộc\x20phải\x20sống.\x20Sống\x20sót\x20qua\x20mùa\x20thu,\x20sống\x20sót\x20dù\x20cho\x20ngôi\x20sao\x20nguyên\x20thủy\x20trong\x20cơ\x20thể\x20mình\x20đã\x20sắp\x20lụi\x20tàn,\x20sống\x20sót,\x20nhờ\x20vào\x20một\x20khả\x20năng\x20kỳ\x20lạ\x20mà\x20nó\x20vừa\x20khám\x20phá:\x20khả\x20năng\x20ghi\x20đè\x20ý\x20thức\x20và\x20bản\x20chất\x20của\x20chính\x20mình\x20lên\x20từng\x20ánh\x20sao\x20đã\x20hấp\x20thụ.\x20Nghĩa\x20là,\x20ngay\x20cả\x20khi\x20ngôi\x20sao\x20nguyên\x20bản\x20bị\x20tắt,\x20thì\x20giấc\x20mơ\x20này…\x20vẫn\x20còn\x20sống.\x0a\x0aTrong\x20một\x20khoảng\x20thời\x20gian\x20không\x20rõ\x20đã\x20qua\x20bao\x20lâu\x20–\x20có\x20thể\x20là\x20vài\x20ngày,\x20hoặc\x20vài\x20tuần\x20ngắn\x20ngủi\x20–\x20tất\x20cả\x20quạ\x20trên\x20bầu\x20trời\x20đều\x20đã\x20lần\x20lượt\x20gục\x20ngã.\x20Tổng\x20cộng\x20là\x20hơn\x20375\x20ngôi\x20sao.\x20Một\x20con\x20số\x20khổng\x20lồ.\x20Và\x20đó\x20chỉ\x20là\x20số\x20lượng\x20những\x20vì\x20sao\x20còn\x20có\x20thể\x20phát\x20sáng.\x0a\x0aMột\x20vài\x20ngôi\x20sao\x20chỉ\x20còn\x20một\x20ngày\x20nữa\x20là\x20lụi,\x20số\x20khác\x20nhiều\x20hơn\x20–\x20ba,\x20bốn,\x20thậm\x20chí\x20năm\x20ngày.\x20Nhưng\x20cộng\x20dồn\x20lại,\x20tất\x20cả\x20đều\x20đủ.\x20Quá\x20đủ.\x20Không\x20chỉ\x20để\x20sống\x20sót\x20qua\x20mùa\x20thu\x20này,\x20mà\x20còn\x20có\x20thể\x20kéo\x20dài\x20thêm\x20đến\x20mùa\x20thu\x20kế\x20tiếp.\x0a\x0aKhi\x20con\x20quạ\x20cuối\x20cùng\x20rơi\x20khỏi\x20vòm\x20trời,\x20làn\x20gió\x20cũng\x20chuyển\x20hẳn\x20sang\x20lạnh\x20giá.\x20Bầu\x20trời\x20im\x20ắng,\x20không\x20một\x20bóng\x20đen\x20lượn\x20lờ,\x20không\x20còn\x20tiếng\x20sải\x20cánh\x20và\x20chẳng\x20có\x20ai\x20đưa\x20tiễn\x20nữa.\x20Và\x20giấc\x20mơ\x20đó\x20hiểu\x20ra\x20rằng,\x20nếu\x20muốn\x20tiếp\x20tục,\x20nó\x20phải\x20trở\x20thành\x20kẻ\x20đưa\x20tiễn,\x20phải\x20thay\x20thế\x20bọn\x20quạ,\x20phải\x20là\x20“tử\x20thần”\x20cho\x20đồng\x20loại\x20mình.\x0a\x0aChúng\x20–\x20những\x20Kyumulus\x20còn\x20lại\x20–\x20vẫn\x20đang\x20trôi\x20nổi\x20trong\x20không\x20trung\x20như\x20cánh\x20hoa\x20chưa\x20biết\x20tàn.\x20Vô\x20lo,\x20vô\x20nghĩ,\x20không\x20biết\x20rằng\x20mùa\x20thu\x20đã\x20đi\x20đến\x20tận\x20đáy.\x20Chúng\x20không\x20thấy\x20lũ\x20quạ\x20đâu\x20cả,\x20nhưng\x20chẳng\x20ai\x20thắc\x20mắc.\x20Bởi\x20với\x20chúng,\x20sự\x20tan\x20biến\x20là\x20chuyện\x20hiển\x20nhiên,\x20là\x20điều\x20vốn\x20phải\x20xảy\x20ra.\x0a\x0aNhưng\x20rồi\x20một\x20trong\x20số\x20chúng\x20tan\x20biến\x20quá\x20đột\x20ngột.\x20Một\x20Kyumulus\x20vừa\x20cười\x20đùa\x20phút\x20trước,\x20bỗng\x20dưng\x20co\x20lại\x20khi\x20ánh\x20sao\x20bị\x20rút\x20khỏi\x20lồng\x20ngực.\x20Một\x20ánh\x20sao\x20nhỏ\x20xíu\x20được\x20kéo\x20ra\x20ngoài,\x20phát\x20ra\x20âm\x20thanh\x20mong\x20manh\x20như\x20tiếng\x20chuông\x20thủy\x20tinh\x20rạn\x20vỡ.\x0a\x0aKẻ\x20ra\x20tay…\x20là\x20một\x20Kyumulus\x20khác.\x20Nhưng\x20trông\x20không\x20còn\x20giống\x20chúng.\x20Gương\x20mặt\x20trông\x20có\x20vẻ\x20sắc\x20sảo\x20hơn,\x20ánh\x20mắt\x20tỉnh\x20táo\x20hơn,\x20thân\x20thể\x20không\x20trôi\x20nổi\x20mà\x20đang…\x20đi\x20bộ.\x20Một\x20điều\x20kỳ\x20dị.\x20Một\x20điều\x20chẳng\x20Kyumulus\x20nào\x20hiểu\x20nổi.\x0a\x0aChúng\x20chỉ\x20kịp\x20thắc\x20mắc\x20trong\x20vài\x20khoảnh\x20khắc.\x20Rồi\x20cũng\x20bị\x20tước\x20lấy\x20ánh\x20sao.\x20Và\x20tan\x20ra\x20như\x20sương.\x20Không\x20ai\x20trong\x20chúng\x20sợ\x20hãi.\x20Không\x20ai\x20phản\x20kháng.\x20Chúng\x20không\x20có\x20bản\x20năng\x20sinh\x20tồn.\x20Không\x20có\x20khả\x20năng\x20chiến\x20đấu.\x20Cũng\x20như\x20ngày\x20nó\x20từng\x20bị\x20quạ\x20rình\x20rập,\x20chẳng\x20một\x20Kyumulus\x20nào\x20đã\x20từng\x20biết\x20phải\x20sợ\x20điều\x20gì.\x0a\x0aCứ\x20thế,\x20từng\x20ngôi\x20sao\x20rời\x20khỏi\x20ngực,\x20từng\x20thân\x20thể\x20tan\x20vào\x20trời.\x20Một\x20mình\x20nó,\x20giữa\x20hàng\x20trăm\x20ánh\x20sao,\x20đã\x20lấy\x20tất\x20cả.\x20Không\x20ai\x20còn\x20lại.\x0a\x0aMùa\x20thu\x20rồi\x20cũng\x20qua,\x20bầu\x20trời\x20dần\x20đổi\x20sắc,\x20bước\x20sang\x20đông\x20với\x20màu\x20xám\x20im\x20lìm.\x20Người\x20dân\x20Vân\x20Quốc\x20(Cloud)\x20–\x20nếu\x20có\x20ai\x20từng\x20quan\x20sát\x20từ\x20xa\x20–\x20sẽ\x20tưởng\x20lũ\x20quạ\x20đã\x20hoàn\x20thành\x20nhiệm\x20vụ,\x20đưa\x20các\x20Kyumulus\x20trở\x20lại\x20trời.\x20Giấc\x20mơ\x20đã\x20kết\x20thúc,\x20như\x20lẽ\x20thường.\x20Cơn\x20mưa\x20sao\x20đã\x20tắt.\x20Lũ\x20mây\x20nhỏ\x20cũng\x20chẳng\x20còn\x20vết\x20tích.\x0a\x0aKhông\x20ai\x20biết.\x20Không\x20ai\x20biết\x20rằng\x20chính\x20giữa\x20vòm\x20trời\x20tĩnh\x20mịch\x20ấy,\x20vẫn\x20còn\x20một\x20giấc\x20mơ\x20đang\x20bước\x20đi.\x20Một\x20giấc\x20mơ\x20đã\x20sống\x20sót…\x20bằng\x20cách\x20nuốt\x20chửng\x20tất\x20cả\x20những\x20giấc\x20mơ\x20khác.\x20Một\x20Kyumulus\x20còn\x20tồn\x20tại\x20–\x20lặng\x20lẽ,\x20âm\x20thầm,\x20và\x20không\x20bao\x20giờ\x20muốn\x20tan\x20biến.','8OeTaHa','Chapter\x205:\x20Among\x20Clouds,\x20Against\x20Clouds','50qOSWHx','105203CQPkja','2025-09-02','1979388jZMEzc','4276875oLAvax'];_0x30d6=function(){return _0x3a7699;};return _0x30d6();}function _0x51fb(_0x4f9436,_0x350ee4){const _0x30d638=_0x30d6();return _0x51fb=function(_0x51fbfd,_0x607873){_0x51fbfd=_0x51fbfd-0x104;let _0x28a4ac=_0x30d638[_0x51fbfd];return _0x28a4ac;},_0x51fb(_0x4f9436,_0x350ee4);}(function(_0x5c069b,_0x5f209b){const _0x4ccee8=_0x51fb,_0x24b9c0=_0x5c069b();while(!![]){try{const _0x2f47be=-parseInt(_0x4ccee8(0x10d))/0x1*(parseInt(_0x4ccee8(0x10a))/0x2)+-parseInt(_0x4ccee8(0x116))/0x3+parseInt(_0x4ccee8(0x107))/0x4+parseInt(_0x4ccee8(0x110))/0x5+parseInt(_0x4ccee8(0x10f))/0x6*(-parseInt(_0x4ccee8(0x105))/0x7)+parseInt(_0x4ccee8(0x112))/0x8*(-parseInt(_0x4ccee8(0x108))/0x9)+parseInt(_0x4ccee8(0x10c))/0xa*(parseInt(_0x4ccee8(0x106))/0xb);if(_0x2f47be===_0x5f209b)break;else _0x24b9c0['push'](_0x24b9c0['shift']());}catch(_0x20dab9){_0x24b9c0['push'](_0x24b9c0['shift']());}}}(_0x30d6,0xa88f1));const dreamRefused={'title':'The\x20Dream\x20That\x20Refused\x20To\x20Vanish','cover':'https://f2.toyhou.se/file/f2-toyhou-se/thumbnails/100002724_Zig.png','published':_0x1e09ae(0x10e),'genre':['Tragedy',_0x1e09ae(0x113),_0x1e09ae(0x115),_0x1e09ae(0x119)],'music':[_0x1e09ae(0x11a)],'summary':'In\x20a\x20fleeting\x20world\x20born\x20from\x20dreams\x20and\x20clouds,\x20fragile\x20beings\x20drift\x20through\x20the\x20passage\x20of\x20seasons,\x20carrying\x20with\x20them\x20both\x20wonder\x20and\x20sorrow.\x20Their\x20existence\x20is\x20as\x20beautiful\x20as\x20it\x20is\x20ephemeral,\x20yet\x20within\x20this\x20fragile\x20cycle\x20lies\x20a\x20question\x20that\x20defies\x20destiny\x20itself:\x20what\x20does\x20it\x20mean\x20to\x20truly\x20live?\x20Amid\x20starlight,\x20ravens,\x20and\x20the\x20quiet\x20breath\x20of\x20autumn\x20skies,\x20a\x20story\x20unfolds\x20-\x20one\x20that\x20lingers\x20between\x20hope\x20and\x20despair,\x20light\x20and\x20shadow,\x20memory\x20and\x20oblivion.','cast':['Xuan\x20Yi'],'chapters':[{'title':_0x1e09ae(0x114),'content':{'en':'','vi':_0x1e09ae(0x111)}},{'title':'Chapter\x202:\x20From\x20Within\x20a\x20Dream','content':{'en':'','vi':'Trong\x20vô\x20vàn\x20những\x20giấc\x20mơ\x20còn\x20chưa\x20định\x20hình\x20được\x20chính\x20mình,\x20có\x20một\x20giấc\x20mơ\x20đã\x20được\x20sinh\x20ra\x20khác\x20biệt.\x20Một\x20giấc\x20mơ\x20đẹp,\x20nhưng\x20tàn\x20nhẫn.\x20Một\x20tia\x20sáng\x20huyền\x20bí,\x20nhưng\x20lại\x20mang\x20trong\x20nó\x20một\x20nỗi\x20buồn\x20nặng\x20trĩu.\x20Không\x20ai\x20biết\x20giấc\x20mơ\x20đó\x20đến\x20từ\x20đâu,\x20chỉ\x20biết\x20rằng,\x20nó\x20mãnh\x20liệt\x20đến\x20mức\x20toàn\x20bộ\x20bầu\x20trời\x20như\x20đã\x20khẽ\x20rung\x20lên\x20khi\x20nó\x20chạm\x20vào\x20mây\x20lần\x20đầu\x20tiên.\x20Nó\x20cũng\x20như\x20bao\x20giấc\x20mơ\x20khác\x20–\x20sẽ\x20tan\x20biến\x20sau\x20vài\x20tháng\x20mùa\x20thu\x20ngắn\x20ngủi.\x20Nhưng\x20tạo\x20hóa,\x20như\x20một\x20trò\x20đùa\x20ác\x20nghiệt,\x20đã\x20ban\x20cho\x20nó\x20thứ\x20không\x20nên\x20có:\x20ý\x20thức.\x0a\x0aKhông\x20chỉ\x20đơn\x20thuần\x20là\x20tồn\x20tại,\x20giấc\x20mơ\x20ấy\x20hiểu\x20rằng\x20mình\x20đang\x20sống.\x20Nó\x20cảm\x20nhận\x20được\x20không\x20gian,\x20thời\x20gian,\x20nỗi\x20cô\x20độc,\x20sự\x20mất\x20mát,\x20cả\x20cái\x20chết\x20đang\x20chờ\x20đợi.\x20Trong\x20khi\x20bao\x20Kyumulus\x20khác\x20vô\x20tư\x20bay\x20lượn,\x20cười\x20đùa\x20giữa\x20mây,\x20thì\x20nó\x20–\x20từ\x20khoảnh\x20khắc\x20được\x20sinh\x20ra\x20–\x20đã\x20sống\x20trong\x20sự\x20sợ\x20hãi\x20âm\x20ỉ.\x20Sợ\x20thời\x20gian\x20trôi\x20qua\x20quá\x20nhanh,\x20sợ\x20khoảnh\x20khắc\x20cuối\x20cùng\x20sẽ\x20đến\x20trước\x20khi\x20nó\x20có\x20thể\x20hiểu\x20hết\x20thế\x20giới,\x20sợ\x20chính\x20sự\x20mong\x20manh\x20mà\x20mình\x20đang\x20mang.\x0a\x0aTại\x20sao\x20đồng\x20loại\x20của\x20nó\x20lại\x20có\x20thể\x20vui\x20vẻ\x20đến\x20thế?\x20Tại\x20sao\x20chúng\x20không\x20sợ\x20hãi,\x20không\x20khóc\x20than,\x20không\x20khát\x20khao\x20được\x20sống\x20lâu\x20hơn\x20chút\x20nữa?\x20Tại\x20sao…\x20chỉ\x20có\x20nó\x20là\x20đau?\x0a\x0aCơ\x20thể\x20giấc\x20mơ\x20này\x20khác\x20biệt:\x20nó\x20có\x20thể\x20đi\x20lại,\x20không\x20cần\x20trôi\x20nổi\x20như\x20những\x20Kyumulus\x20khác.\x20Nó\x20không\x20biết\x20mệt,\x20có\x20thể\x20cảm\x20nhận\x20nhiệt\x20độ,\x20âm\x20thanh,\x20sự\x20chuyển\x20động\x20của\x20gió\x20và\x20ánh\x20sáng.\x20Nó\x20biết\x20lùi\x20lại\x20khi\x20có\x20nguy\x20hiểm,\x20biết\x20né\x20tránh,\x20biết\x20che\x20chắn\x20bản\x20thân\x20–\x20như\x20thể\x20bản\x20năng\x20sinh\x20tồn\x20đã\x20được\x20lập\x20trình\x20sẵn\x20vào\x20lớp\x20mây\x20mềm\x20ấy.\x20Từ\x20những\x20giây\x20phút\x20đầu\x20tiên,\x20nó\x20đã\x20không\x20thể\x20lạc\x20quan.\x20Không\x20thể\x20hòa\x20mình\x20vào\x20sự\x20ngây\x20thơ\x20vô\x20thường\x20của\x20đồng\x20loại.\x20Bởi\x20vì\x20nó\x20bị\x20nguyền\x20–\x20nguyền\x20rủa\x20bằng\x20chính\x20món\x20quà\x20tàn\x20độc\x20mang\x20tên\x20“ý\x20thức”.\x0a\x0aVà\x20vì\x20thế…\x20nó\x20không\x20thể\x20tận\x20hưởng\x20cuộc\x20sống\x20như\x20bao\x20Kyumulus\x20khác.\x20Tồn\x20tại\x20của\x20nó,\x20từ\x20đầu\x20đến\x20cuối,\x20chỉ\x20là\x20một\x20chuỗi\x20những\x20ngày\x20dài\x20đau\x20khổ,\x20tuyệt\x20vọng\x20và\x20sợ\x20hãi\x20tột\x20độ.\x20Hỏi\x20rằng,\x20cái\x20đẹp,\x20cái\x20mong\x20manh,\x20cái\x20tinh\x20khiết\x20vốn\x20dĩ\x20được\x20xem\x20là\x20linh\x20hồn\x20của\x20một\x20giấc\x20mơ\x20–\x20liệu\x20còn\x20có\x20thể\x20gọi\x20là\x20đẹp\x20đẽ\x20nữa\x20hay\x20không,\x20nếu\x20sinh\x20vật\x20mang\x20hình\x20hài\x20giấc\x20mơ\x20ấy\x20không\x20còn\x20tin\x20vào\x20ánh\x20sáng?\x0a\x0aNó\x20không\x20bay.\x20Không\x20xoay\x20vòng\x20trong\x20gió,\x20không\x20phát\x20ra\x20âm\x20thanh\x20vui\x20nhộn\x20như\x20những\x20sinh\x20thể\x20nhỏ\x20khác.\x20Nó\x20chỉ\x20đứng\x20đó,\x20không\x20làm\x20gì\x20cả,\x20suy\x20tư\x20trước\x20những\x20thứ\x20mà\x20giấc\x20mơ\x20này\x20luôn\x20lo\x20sợ.\x0a\x0aNó\x20không\x20tham\x20gia\x20trò\x20chơi.\x20Không\x20nhìn\x20về\x20phía\x20đàn\x20quạ\x20đang\x20sà\x20xuống\x20thu\x20nhặt\x20những\x20vì\x20sao\x20rơi.\x20Không\x20ngẩng\x20đầu\x20theo\x20dõi\x20ánh\x20sao\x20băng\x20vừa\x20quét\x20qua\x20chân\x20trời.\x20Nó\x20chỉ\x20lặng\x20lẽ\x20quan\x20sát\x20–\x20từng\x20Kyumulus\x20một\x20–\x20bằng\x20ánh\x20mắt…\x20mà\x20không\x20ai\x20trong\x20số\x20chúng\x20từng\x20có.\x0a\x0aMột\x20ánh\x20mắt\x20biết\x20phân\x20biệt.\x20Biết\x20đau.\x20Biết\x20khao\x20khát\x20sống.\x0a\x0aKhông\x20ai\x20để\x20ý\x20đến\x20điều\x20đó.\x20Vì\x20Kyumulus\x20không\x20biết\x20thế\x20nào\x20là\x20“khác\x20thường”.\x20Không\x20ai\x20trong\x20số\x20chúng\x20hiểu\x20được\x20khái\x20niệm\x20ấy.\x20Chúng\x20chỉ\x20biết\x20mộng\x20mơ,\x20và\x20rồi\x20tan\x20biến.\x20Nếu\x20có\x20một\x20cá\x20thể\x20nào\x20đứng\x20lại,\x20chúng\x20sẽ\x20nghĩ\x20đó\x20là\x20vì\x20một\x20cơn\x20gió\x20thổi\x20ngược.\x20Nếu\x20có\x20ánh\x20mắt\x20khác\x20đi,\x20chúng\x20sẽ\x20nghĩ\x20đó\x20là\x20do\x20ánh\x20sao\x20phản\x20chiếu\x20từ\x20góc\x20trời\x20gần\x20hơn.\x20Chúng\x20không\x20hiểu\x20–\x20và\x20cũng\x20chẳng\x20cần\x20hiểu.\x0a\x0aCuối\x20thu\x20luôn\x20là\x20một\x20mùa\x20vắng\x20lặng.\x20Những\x20lớp\x20mây\x20dày\x20phủ\x20khắp\x20lưng\x20trời,\x20như\x20một\x20tấm\x20chăn\x20mỏng\x20mềm\x20ai\x20đó\x20đã\x20để\x20quên.\x20Lá\x20không\x20còn\x20theo\x20gió\x20bay\x20lên\x20mà\x20chỉ\x20rơi\x20xuống,\x20nặng\x20nề,\x20rồi\x20đọng\x20lại\x20trên\x20thân\x20cây\x20đã\x20ngả\x20sang\x20màu\x20xám\x20bạc.\x20Bầu\x20trời\x20không\x20còn\x20đổi\x20sắc\x20–\x20nó\x20cứ\x20đục\x20ngầu,\x20lạnh\x20lẽo,\x20vẩn\x20vơ\x20màu\x20tro\x20mãi\x20như\x20vậy.\x0a\x0aChỉ\x20có\x20những\x20tiếng\x20quạ\x20kêu\x20lên\x20mãnh\x20liệt,\x20đầy\x20sức\x20sống,\x20thi\x20thoảng\x20phá\x20vỡ\x20sự\x20im\x20lặng.\x20Lông\x20vũ\x20đen\x20rơi\x20xuống,\x20đôi\x20khi\x20đậu\x20vào\x20cánh\x20của\x20một\x20Kyumulus\x20rồi\x20lại\x20bị\x20gió\x20cuốn\x20đi.\x20Nhưng\x20với\x20cá\x20thể\x20đang\x20đứng\x20kia,\x20âm\x20thanh\x20đó\x20không\x20hề\x20gây\x20kinh\x20động\x20–\x20nó\x20chỉ\x20khiến\x20sinh\x20vật\x20ấy\x20chớp\x20mắt\x20chậm\x20hơn\x20một\x20nhịp.\x0a\x0aKyumulus\x20là\x20sinh\x20vật\x20được\x20sinh\x20ra\x20từ\x20giấc\x20mơ\x20–\x20ai\x20cũng\x20biết\x20điều\x20đó.\x20Và\x20mùa\x20thu\x20là\x20mùa\x20bội\x20thu\x20của\x20những\x20giấc\x20mơ\x20chưa\x20trọn,\x20chưa\x20kịp\x20tỉnh,\x20chưa\x20kịp\x20lãng\x20quên.\x20Chúng\x20tụ\x20lại\x20thành\x20mây,\x20và\x20từ\x20mây,\x20Kyumulus\x20hình\x20thành.\x0a\x0aNhững\x20sinh\x20thể\x20nhỏ\x20bé\x20ấy\x20không\x20có\x20tên,\x20không\x20có\x20quá\x20khứ,\x20không\x20có\x20tương\x20lai\x20–\x20chỉ\x20có\x20hiện\x20tại.\x20Một\x20hiện\x20tại\x20mềm\x20mại,\x20ấm\x20áp,\x20và\x20đẹp\x20đẽ\x20như\x20ánh\x20sáng\x20của\x20một\x20vì\x20tinh\x20tú\x20đang\x20chớp\x20mắt\x20lần\x20cuối\x20trong\x20khoảnh\x20khắc\x20cuối\x20cùng.'}},{'title':_0x1e09ae(0x104),'content':{'en':'','vi':'Ngày\x20qua\x20ngày,\x20giấc\x20mơ\x20ấy\x20vẫn\x20cứ\x20đứng\x20đó,\x20chỉ\x20bất\x20động\x20và\x20lặng\x20thinh,\x20như\x20thể\x20chính\x20nó\x20đã\x20bị\x20đóng\x20băng\x20giữa\x20một\x20bầu\x20trời\x20thu\x20không\x20có\x20lối\x20thoát.\x20Những\x20Kyumulus\x20còn\x20lại,\x20chúng\x20vẫn\x20bay\x20lượn\x20vô\x20tư,\x20đùa\x20giỡn,\x20và\x20cười\x20vang\x20giữa\x20tầng\x20mây\x20nhạt,\x20nơi\x20ánh\x20nắng\x20dìu\x20dịu\x20xuyên\x20qua\x20từng\x20vệt\x20sương\x20trôi.\x20Nhưng\x20kẻ\x20ấy,\x20giấc\x20mơ\x20đơn\x20độc\x20kia,\x20chỉ\x20biết\x20nhìn,\x20chỉ\x20biết\x20lặng\x20im.\x0a\x0aNó\x20không\x20cảm\x20thấy\x20gì\x20cả.\x20Không\x20hạnh\x20phúc,\x20không\x20tò\x20mò,\x20không\x20vui\x20vẻ.\x20Chỉ\x20có\x20nỗi\x20buốt\x20lạnh\x20trong\x20lòng\x20—\x20thứ\x20cảm\x20giác\x20không\x20tồn\x20tại\x20ở\x20bất\x20kỳ\x20Kyumulus\x20nào\x20khác.\x20Nếu\x20một\x20giấc\x20mơ\x20sinh\x20ra\x20để\x20đẹp\x20đẽ,\x20thì\x20giấc\x20mơ\x20này\x20đã\x20lạc\x20loài\x20giữa\x20sự\x20mong\x20manh\x20của\x20thế\x20giới.\x0a\x0aPhải\x20chăng\x20nó\x20đã\x20tuyệt\x20vọng\x20đến\x20độ\x20quên\x20luôn\x20cả\x20lý\x20do\x20mình\x20tồn\x20tại?\x20Hay\x20là\x20vì\x20nó\x20quá\x20tỉnh\x20táo,\x20để\x20nhìn\x20thấy\x20cái\x20kết\x20từ\x20lúc\x20bắt\x20đầu?\x20Không\x20ai\x20biết.\x20Cũng\x20không\x20ai\x20quan\x20tâm.\x20Những\x20đám\x20mây\x20cứ\x20trôi,\x20những\x20tiếng\x20cười\x20cứ\x20vang\x20vọng,\x20còn\x20nó\x20thì\x20chỉ\x20lặng\x20lẽ\x20đứng\x20nhìn.\x20Cơ\x20thể\x20mềm\x20như\x20khói,\x20lông\x20tơ\x20như\x20tuyết,\x20mà\x20linh\x20hồn\x20lại\x20nặng\x20trĩu\x20như\x20đá.\x0a\x0a“Ta\x20không\x20hề\x20cảm\x20nhận\x20được\x20điều\x20đó.”\x0a\x0aTrước\x20cảnh\x20sắc\x20của\x20trời\x20thu,\x20nó\x20thì\x20thầm\x20như\x20vậy.\x20Lá\x20vàng\x20xoay\x20tròn\x20giữa\x20khoảng\x20không,\x20ánh\x20sáng\x20dịu\x20dàng\x20như\x20đang\x20vẽ\x20nên\x20bức\x20tranh\x20cuối\x20cùng.\x20Cảnh\x20thì\x20đẹp.\x20Nhưng\x20trái\x20tim\x20nó\x20chỉ\x20có\x20một\x20khoảng\x20trống.\x0a\x0aBất\x20ngờ,\x20một\x20giấc\x20mơ\x20khác\x20bay\x20đến\x20gần.\x20Nhỏ\x20nhắn,\x20ngây\x20ngô,\x20trong\x20trẻo.\x20Nó\x20trông\x20có\x20vẻ\x20ngạc\x20nhiên\x20khi\x20thấy\x20đồng\x20loại\x20đứng\x20yên\x20một\x20chỗ,\x20không\x20tham\x20gia\x20cùng\x20đám\x20đông.\x20Nó\x20ríu\x20rít\x20phát\x20ra\x20những\x20âm\x20thanh\x20đặc\x20trưng\x20của\x20loài\x20Kyumulus,\x20xen\x20lẫn\x20giữa\x20tiếng\x20chuông\x20gió\x20và\x20tiếng\x20cười\x20hạnh\x20phúc.\x20Không\x20phải\x20tiếng\x20nói,\x20nhưng\x20là\x20một\x20kiểu\x20ngôn\x20ngữ\x20lặng\x20thầm,\x20mà\x20bất\x20kỳ\x20giấc\x20mơ\x20nào\x20cũng\x20hiểu.\x0a\x0aNó\x20chỉ\x20thắc\x20mắc\x20tại\x20sao\x20giấc\x20mơ\x20kia\x20lại\x20đăm\x20chiêu\x20đến\x20vậy?\x20Không\x20hề\x20buộc\x20tội\x20hay\x20thương\x20hại.\x20Nhưng\x20nó\x20cũng\x20không\x20cần\x20phải\x20hiểu.\x20Vì\x20đối\x20với\x20nó,\x20cuộc\x20sống\x20ngắn\x20ngủi\x20đâu\x20cần\x20phải\x20suy\x20tư\x20đến\x20vậy?\x20Chỉ\x20cần\x20cười,\x20chỉ\x20cần\x20chơi,\x20rồi\x20biến\x20mất.\x20Có\x20gì\x20sai?\x0a\x0aKẻ\x20còn\x20lại\x20thì\x20khác.\x20Nó\x20muốn\x20hiểu.\x20Nó\x20hỏi:\x20“Ngươi\x20muốn\x20ta\x20chơi\x20cùng\x20ngươi?”\x0a\x0aGiấc\x20mơ\x20nhỏ\x20vui\x20mừng\x20đáp\x20lại\x20bằng\x20một\x20vòng\x20xoay\x20giữa\x20tầng\x20mây.\x20Thế\x20là\x20cả\x20hai\x20ở\x20cạnh\x20nhau.\x20Lúc\x20đầu\x20chỉ\x20là\x20những\x20khoảnh\x20khắc\x20lặng\x20lẽ,\x20nhưng\x20rồi\x20chúng\x20dần\x20chạm\x20vào\x20được\x20nhau\x20–\x20như\x20hai\x20mảnh\x20vụn\x20của\x20cùng\x20một\x20ngôi\x20sao\x20chưa\x20kịp\x20hoàn\x20thiện.\x20Và\x20từ\x20đó,\x20mỗi\x20ngày\x20trôi\x20qua\x20lại\x20khiến\x20nó\x20cảm\x20thấy…\x20dễ\x20thở\x20hơn\x20đôi\x20chút.\x0a\x0aNó\x20không\x20ngừng\x20nghĩ\x20đến\x20giới\x20hạn.\x20Nhưng\x20trong\x20lúc\x20này,\x20trong\x20mảnh\x20ký\x20ức\x20mong\x20manh\x20ấy,\x20nó\x20được\x20phép\x20lơ\x20đễnh.\x20Chỉ\x20một\x20lần.\x0a\x0a“Ta\x20mong\x20ngươi\x20và\x20ta\x20có\x20thể\x20sống\x20mãi\x20cùng\x20nhau.”\x0a\x0aMột\x20câu\x20nói\x20đơn\x20giản\x20nhưng\x20tràn\x20đầy\x20ước\x20vọng.\x20Kẻ\x20thốt\x20ra\x20câu\x20ấy\x20biết\x20rõ\x20điều\x20đó\x20là\x20không\x20thể,\x20nhưng\x20vẫn\x20nói.\x20Vì\x20trong\x20khoảnh\x20khắc\x20ấy,\x20nó\x20thực\x20sự\x20quý\x20người\x20bạn\x20nhỏ\x20kia.\x0a\x0aVà\x20người\x20bạn\x20ấy\x20cũng\x20hiểu\x20điều\x20đó.\x20Dù\x20không\x20thể\x20nói,\x20nhưng\x20ánh\x20nhìn\x20đã\x20thay\x20cho\x20vạn\x20lời.\x20Đó\x20là\x20ngày\x20thứ\x20hai\x20mươi.\x20Và\x20lần\x20đầu\x20tiên,\x20trong\x20tất\x20cả\x20khoảng\x20thời\x20gian\x20tồn\x20tại\x20của\x20mình,\x20nó\x20đã\x20mỉm\x20cười.'}},{'title':_0x1e09ae(0x118),'content':{'en':'','vi':_0x1e09ae(0x117)}},{'title':_0x1e09ae(0x10b),'content':{'en':'','vi':_0x1e09ae(0x109)}}]};
